import React, { useState, useRef, useEffect } from 'react';
import './MorphemeHighlighter.css';
import apiService from '../services/api';

const MorphemeHighlighter = () => {
  const [text, setText] = useState('');
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [selectedWord, setSelectedWord] = useState('');
  const [popupPosition, setPopupPosition] = useState({ x: 0, y: 0 });
  const [showPopup, setShowPopup] = useState(false);
  const [isEditMode, setIsEditMode] = useState(true);
  const [selectedWordElement, setSelectedWordElement] = useState(null);
  const textAreaRef = useRef(null);

  const handleTextChange = (e) => {
    setText(e.target.value);
    setAnalysis(null);
    setError(null);
  };

  const handleWordClick = async (word, event) => {
    if (!word || word.trim() === '') return;

    // Remove previous selection
    if (selectedWordElement) {
      selectedWordElement.classList.remove('selected');
    }

    // Add selection to current word
    event.target.classList.add('selected');
    setSelectedWordElement(event.target);

    const rect = event.target.getBoundingClientRect();
    setPopupPosition({
      x: rect.left + rect.width / 2,
      y: rect.bottom + 10
    });

    setSelectedWord(word);
    setShowPopup(true);
    setLoading(true);
    setError(null);

    try {
      const result = await apiService.analyzeMorpheme(word);
      setAnalysis(result);
    } catch (err) {
      console.error('Failed to analyze word:', err);
      setError('Failed to analyze word. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleClickOutside = (event) => {
    if (textAreaRef.current && !textAreaRef.current.contains(event.target)) {
      setShowPopup(false);
      // Remove selection when clicking outside
      if (selectedWordElement) {
        selectedWordElement.classList.remove('selected');
        setSelectedWordElement(null);
      }
    }
  };

  useEffect(() => {
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [selectedWordElement]);

  const renderTextWithClickableWords = () => {
    if (!text) return null;

    const words = text.split(/(\s+)/);
    return words.map((word, index) => {
      if (word.trim() === '') {
        return <span key={index}>{word}</span>;
      }

      return (
        <span
          key={index}
          className="clickable-word"
          onClick={(e) => handleWordClick(word, e)}
          title={`Click to analyze: ${word}`}
        >
          {word}
        </span>
      );
    });
  };

  return (
    <div className="container">
      <div className="header">
        <h1>
          <span style={{ display: 'inline-flex', alignItems: 'center', gap: '8px' }}>
            üìö Morpheme Highlighter
          </span>
        </h1>
        <p>Highlight any word or section to break it down or summarize.</p>
      </div>

      <div className="content">
        <div className="text-input-section">
          <label htmlFor="text-input" className="input-label">
            Paste or type your text here:
          </label>
          <div className="text-display" ref={textAreaRef}>
            {!isEditMode && text ? (
              <div className="clickable-text">
                {renderTextWithClickableWords()}
              </div>
            ) : (
              <textarea
                id="text-input"
                className="text-input"
                placeholder="Paste or type your text here...&#10;&#10;Try highlighting a word to see its morphemes, or highlight a section for a summary."
                value={text}
                onChange={handleTextChange}
              />
            )}
          </div>

          <div className="instructions">
            üí° Highlight a word to see its morphemes ‚Äî or highlight a section to get a summary.
          </div>

          <div className="button-group">
            {text && (
              <>
                <button 
                  className="mode-button"
                  onClick={() => setIsEditMode(!isEditMode)}
                >
                  {isEditMode ? 'üìñ View Clickable Text' : '‚úèÔ∏è Edit Text'}
                </button>
                <button 
                  className="clear-button"
                  onClick={() => {
                    setText('');
                    setAnalysis(null);
                    setError(null);
                    setShowPopup(false);
                    setIsEditMode(true);
                    if (selectedWordElement) {
                      selectedWordElement.classList.remove('selected');
                      setSelectedWordElement(null);
                    }
                  }}
                >
                  Clear Text
                </button>
              </>
            )}
          </div>
        </div>

        {/* Results Area */}
        <div className="results-area">
          {!analysis && !loading ? (
            <div className="results-placeholder">
            </div>
          ) : (
            <div className="results-content">
              {loading && (
                <div className="loading">
                  <div className="spinner"></div>
                  <p>Analyzing word structure...</p>
                </div>
              )}

              {error && (
                <div className="error">
                  <p>{error}</p>
                </div>
              )}

              {analysis && !loading && (
                <>
                  <h3>üìö Analysis Results</h3>
                  <div className="morpheme-breakdown">
                    <h4>Morpheme Breakdown:</h4>
                    <div className="morphemes">
                      {analysis.prefix && (
                        <div className="morpheme prefix">
                          <span className="part">{analysis.prefix.part}</span>
                          <span className="meaning">{analysis.prefix.meaning}</span>
                        </div>
                      )}
                      {analysis.root && (
                        <div className="morpheme root">
                          <span className="part">{analysis.root.part}</span>
                          <span className="meaning">{analysis.root.meaning}</span>
                        </div>
                      )}
                      {analysis.suffix1 && (
                        <div className="morpheme suffix">
                          <span className="part">{analysis.suffix1.part}</span>
                          <span className="meaning">{analysis.suffix1.meaning}</span>
                        </div>
                      )}
                      {analysis.suffix2 && (
                        <div className="morpheme suffix">
                          <span className="part">{analysis.suffix2.part}</span>
                          <span className="meaning">{analysis.suffix2.meaning}</span>
                        </div>
                      )}
                    </div>
                  </div>

                  {analysis.example && (
                    <div className="example">
                      <h4>üí° Example Usage:</h4>
                      <p>"{analysis.example}"</p>
                    </div>
                  )}

                  {analysis.related && analysis.related.length > 0 && (
                    <div className="related-words">
                      <h4>üîó Related Words:</h4>
                      <div className="word-list">
                        {analysis.related.map((word, index) => (
                          <span key={index} className="related-word">{word}</span>
                        ))}
                      </div>
                    </div>
                  )}
                </>
              )}
            </div>
          )}
        </div>

        {showPopup && (
          <div 
            className="analysis-popup"
            style={{
              left: `${popupPosition.x}px`,
              top: `${popupPosition.y}px`
            }}
          >
            <div className="popup-header">
              <h3>Analysis: "{selectedWord}"</h3>
              <button 
                className="close-button"
                onClick={() => {
                  setShowPopup(false);
                  if (selectedWordElement) {
                    selectedWordElement.classList.remove('selected');
                    setSelectedWordElement(null);
                  }
                }}
              >
                √ó
              </button>
            </div>

            {loading && (
              <div className="loading">
                <div className="spinner"></div>
                <p>Analyzing word structure...</p>
              </div>
            )}

            {error && (
              <div className="error">
                <p>{error}</p>
              </div>
            )}

            {analysis && !loading && (
              <div className="analysis-content">
                <div className="morpheme-breakdown">
                  <h4>üìö Morpheme Breakdown:</h4>
                  <div className="morphemes">
                    {analysis.prefix && (
                      <div className="morpheme prefix">
                        <span className="part">{analysis.prefix.part}</span>
                        <span className="meaning">{analysis.prefix.meaning}</span>
                      </div>
                    )}
                    {analysis.root && (
                      <div className="morpheme root">
                        <span className="part">{analysis.root.part}</span>
                        <span className="meaning">{analysis.root.meaning}</span>
                      </div>
                    )}
                    {analysis.suffix1 && (
                      <div className="morpheme suffix">
                        <span className="part">{analysis.suffix1.part}</span>
                        <span className="meaning">{analysis.suffix1.meaning}</span>
                      </div>
                    )}
                    {analysis.suffix2 && (
                      <div className="morpheme suffix">
                        <span className="part">{analysis.suffix2.part}</span>
                        <span className="meaning">{analysis.suffix2.meaning}</span>
                      </div>
                    )}
                  </div>
                </div>

                {analysis.example && (
                  <div className="example">
                    <h4>üí° Example Usage:</h4>
                    <p>"{analysis.example}"</p>
                  </div>
                )}

                {analysis.related && analysis.related.length > 0 && (
                  <div className="related-words">
                    <h4>üîó Related Words:</h4>
                    <div className="word-list">
                      {analysis.related.map((word, index) => (
                        <span key={index} className="related-word">{word}</span>
                      ))}
                    </div>
                  </div>
                )}
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default MorphemeHighlighter;
